import React from "react"

class AssignmentsData extends React.Component {
    constructor() {
        super()
        this.state= {
           complete: false
        }
    }


    render() {
        const assignmentsDataStyling = {backgroundColor: this.state.complete ? "green" : "transparent", color: this.state.complete ? "white" : "black" , padding: "24px", fontSize: "18px", transition: "all 0.7s"}
        return(
            <div className="assignmentsData-item" style={assignmentsDataStyling}>
                <label className="assignmentsData-item-btn" for="c">
                    Complete Assignment
                </label>
                <input type="checkbox" className="assignmentsData-item-complete" id="c" checked={this.props.complete} onChange={()=>this.props.changeChecked(this.props.id)}/>
                <h2>{this.props.title}</h2>
                <p>Assignment Given On: {this.props.setOn}</p>
                <p>Assignment Deadline: {this.props.deadline}</p>
                <p>Assignment ID: {this.props.id}</p>
            </div>
        )
    }
}
export default AssignmentsData
