import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class AssignmentTracker extends React.Component {
  constructor() {
      super()
      this.state= {
         assignmentsJSON : [],
         headerImg: "https://poster.keepcalmandposters.com/8738135.jpg"
      }
  }

  componentDidMount() {
    fetch('https://beehiveapi.lionhearttrust.org.uk/v3.5/planner/test/assignments?pageIndex=0&pageSize=20')
    .then(response => response.json())
    .then(response => {
          const {assignments} = response.items
          this.setState({
            assignmentsJSON: assignments
          })  
          this.getListItems
      })
  }

  getListItems() {
      const assignmentListItems = this.state.assignmentsJSON.map((meme)=> <li key={meme.id}>{meme}</li>);
      console.log(this.state.assignmentsJSON)
  }

  render() {

      return (
          <div class="row">
              <header>
                  <img class="center-block img-responsive w-25" src={this.state.headerImg} alt="Keep Calm and Do Your Assignment" />
                  <h1 class="text-center">Homework Assignment Tracker</h1>
              </header>

              <div class="col-md-6 assignment-items">
                <ul>
                  {assignmentListItems}
                </ul>
              </div>

              <div class="col-md-6">
                <h1> TESTIN' BITCH</h1>
              </div>
          </div>
      )
  }
}

ReactDOM.render(
  <AssignmentTracker/>,
  document.getElementById('root')
);




