import React from "react"

class AssignmentsData extends React.Component {
    constructor() {
        super()
        this.state= {
           complete: false
        }
    }

    changeChecked = () => { 
        this.setState({
            complete: this.state.complete ? false : true
        })
    }

    render() {
        const assignmentsDataStyling = {backgroundColor: this.state.complete ? "green" : "transparent", color: this.state.complete ? "white" : "black" , padding: "24px", fontSize: "18px", transition: "all 0.7s"}
        return(
            <div className="assignmentsData-item col-md-6 assignment-items-details"  style={assignmentsDataStyling}>
                <label className="assignmentsData-item-btn" htmlFor="c">
                    Complete Assignment
                </label>
                <input type="checkbox" className="assignmentsData-item-complete" id="c" checked={this.state.complete} onChange={this.changeChecked}/>
                <h2>{this.props.title}</h2>
                <p>Assignment Given On: {this.props.setOn}</p>
                <p>Assignment Deadline: {this.props.deadline}</p>
                <p>Assignment ID: {this.props.id}</p>
            </div>
        )
    }
}
export default AssignmentsData
